package Lecture7;

public class BitwiseOperatorOne2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 2;
		
//		int c = a&b;//2
//		int c = a | b;//10
		System.out.println(a&b);
		System.out.println(a|b);
		System.out.println(a^b);
		System.out.println(~a);
	}

}
